import React, { useState } from 'react';
import Nav from "./Nav"
import Calendar from 'react-calendar';
import './Calendar.css'
import styles from "./Record.module.css"


function Record() {
  const [date, setDate] = useState(new Date());
  const [amount, setAmount] = useState("")
  const [category, setCategory] = useState("")
  const [records, setRecords] = useState([])

  console.log(records)
  const handleSubmit = (e) => {
    e.preventDefault()
    setRecords(records => [{date: date.toDateString(), Amout: amount, Category: category}, ... records])
    setAmount("")
    setCategory("")
  }

  return (
    <>
      <Nav></Nav>
      <br />
      <div className={""}>
        <Calendar onChange={setDate} value={date} />
      </div>
      <div>
        {records.length === 0 ?
          <button>新增一筆紀錄</button> :
          <>
          {console.log(records.filter(i => i.date === date.toDateString()))}
          {records.filter(i => i.date === date.toDateString()).map((record,index) => 
            <div key={index}>
              <span>{record.category}</span>
              <span>{record.amount}</span>
            </div>
            )}
          <button>新增紀錄</button>
          </>

        }
      </div>
      <div>
        <div>{date.toDateString()}</div>
        <form onSubmit={handleSubmit}>
          <label htmlFor="amount">金額：</label>
          <input
            id="amount"
            type="number"
            onChange={(e) => setAmount(e.target.value)}
            value={amount}
            required /><br />
          <label htmlFor="category">類型：</label>
          <input 
            id="category" 
            type="text" 
            onChange={(e) => setCategory(e.target.value)}
            value={category}
            required /><br />
          <input type="submit" />
        </form>
      </div>
    </>
  );
}

export default Record
