import React from 'react';
import { useLocation } from 'react-router-dom'
import './Banner.css'

function Banner() {
  const location = useLocation()
  const header = (location) => {
    switch (location.pathname.split('/')[3]) {
      case 'new-arrivals':
        return 'New Arrivals'
        break;
      case 'home-bath':
        return 'Home + Bath'
        break;
      case 'kitchen-dinning':
        return 'Kitchen + Dinning'
        break;
      case 'office-craft':
        return 'Office + Craft'
        break;
      case 'personal-goods':
        return 'Personal Goods'
        break;



      default:
        break;
    }
  }

  return (
    <div className='banner'>
      <div className='banner-location'>{header(location)}</div>
      <div>
        {/* <img src={require(`../img/cover/${location.pathname.split('/')[3]}.jpg`)} alt="" /> */}
      </div>

    </div>
  );
}

export default Banner;
