import React, { useState } from 'react';
import Nav from "./Nav"
import Calendar from 'react-calendar';
import { useRecordsContext } from '../context/RecordsContext';
import './Calendar.css'
import styles from "./Record.module.css"


function Record() {
  const [date, setDate] = useState(new Date());
  const [category, setCategory] = useState("飲食")
  const [amount, setAmount] = useState("")
  const [memo, setMemo] = useState("")
  const {records, setRecords} = useRecordsContext()

  const handleSubmit = (e) => {
    e.preventDefault()
    setRecords(records => [...records, {id: records.length+1, date: date.toDateString(), category: category, memo: memo,amount: amount}])
    console.log()
    setCategory(e.target[0].options[e.target[0].selectedIndex].value)
    setAmount("")
    setMemo("")
  }

  return (
    <>
      <Nav></Nav>
      <br />
      <div className={""}>
        <Calendar onChange={setDate} value={date} />
      </div>
      <div>{date.toDateString()}</div>
      <div>
          {records.filter(i => i.date === date.toDateString()).map((record,index) => 
            <div key={index}>
              <span>{record.category}</span>
              <span>{record.memo}</span>
              <span>{record.amount}</span>
              <button>edit</button>
            </div>
            )}
      </div>
      <div>

        <form onSubmit={handleSubmit}>
        <label htmlFor="category"></label>
          <select name="" id="category" onChange={(e) => {
            setCategory(e.target.options[e.target.selectedIndex].value);
            }}>
            <option value="飲食">飲食</option>
            <option value="日用">日用</option>
            <option value="交通">交通</option>
            <option value="水電">水電</option>
          </select>
          <label htmlFor="memo"></label>
          <input
            id="memo"
            type="text"
            onChange={(e) => setMemo(e.target.value)}
            value={memo} 
            placeholder='備註'/>
          <label htmlFor="amount"></label>
          <input
            id="amount"
            type="number"
            onChange={(e) => setAmount(e.target.value)}
            value={amount}
            required 
            placeholder='請輸入金額' />
          <input type="submit" />
        </form>
      </div>
    </>
  );
}

export default Record
