import { createContext, useState } from "react";

export const CartContext = createContext()

export const CartContextProvider = ( {children} ) => {
  const [cartItems, setCartItems] = useState([])
  const increaseCartItems = (id) => {
    if (cartItems.find(i => i.id === id) == null) {
      setCartItems([...cartItems, {id, quantity: 1}])
    } else {
      setCartItems.map(cur => {
        if (cur.id === id) {
          return {...cur, quantity: cur.quantity+1}
        } else {
          return cur
        }
      })
    }
  }
  const decreaseItems = (id) => {
    if (cartItems.find(i => i.id === id)?.quantity) {
      setCartItems([...cartItems, {id, quantity: 1}])
    } else {
      setCartItems.map(cur => {
        if (cur.id === id) {
          return {...cur, quantity: cur.quantity+1}
        } else {
          return cur
        }
      })
    }
  }



  return  <CartContext.Provider value={{increaseCartItems,}}>
            {children} 
          </CartContext.Provider>
}
