import React, { useEffect } from 'react';
import calculatorSlice from '../redux/calculatorSlice';
import { useSelector, useDispatch } from 'react-redux';
import styles from './Calculator.module.css'

function Calculator() {
  const [procedure, inputOutput] = useSelector((state) => {
    return [state.calculator.procedure, state.calculator.inputOutput]
  })
  const limit = useSelector((state) => {
    return state.calculator.digitLimit
  })
  const dispatch = useDispatch()
  useEffect(() => {
    if (limit === true) {
      document.getElementById('input-output').innerHTML = 'Digit limit met'
      setTimeout(() => {
        document.getElementById('input-output').innerHTML = inputOutput
        dispatch(calculatorSlice.actions.setLimit())
      },1200) 
    }
  })

  return (
    <div className={styles.wrap}>
      <div className={styles.container}>
        <div className={styles.output}>
          <div className={styles.procedure}>{procedure}</div>
          <div id='input-output' className={styles.inputOutput}>{inputOutput}</div>
        </div>
        <button className={styles.AC} onClick={() => dispatch(calculatorSlice.actions.reset())}>AC</button>
        <button onClick={() => dispatch(calculatorSlice.actions.operator('/'))}>/</button>
        <button onClick={() => dispatch(calculatorSlice.actions.operator('*'))}>x</button>
        <br />
        <button onClick={() => dispatch(calculatorSlice.actions.input('7'))}>7</button>
        <button onClick={() => dispatch(calculatorSlice.actions.input('8'))}>8</button>
        <button onClick={() => dispatch(calculatorSlice.actions.input('9'))}>9</button>
        <button onClick={() => dispatch(calculatorSlice.actions.operator('-'))}>-</button>
        <br />
        <button onClick={() => dispatch(calculatorSlice.actions.input('4'))}>4</button>
        <button onClick={() => dispatch(calculatorSlice.actions.input('5'))}>5</button>
        <button onClick={() => dispatch(calculatorSlice.actions.input('6'))}>6</button>
        <button onClick={() => dispatch(calculatorSlice.actions.operator('+'))}>+</button>
        <br />
        <div className={styles.flexbox}>
          <div className={styles.flexleft}>
            <button onClick={() => dispatch(calculatorSlice.actions.input('1'))}>1</button>
            <button onClick={() => dispatch(calculatorSlice.actions.input('2'))}>2</button>
            <button onClick={() => dispatch(calculatorSlice.actions.input('3'))}>3</button>
            <br />
            <button className={styles.zero} onClick={() => dispatch(calculatorSlice.actions.input('0'))}>0</button>
            <button onClick={() => dispatch(calculatorSlice.actions.deciaml('.'))}>.</button>
          </div>    
          <div className={styles.flexright}>
            <button className={styles.equal} onClick={() => dispatch(calculatorSlice.actions.calculate())}>=</button>
            </div>  
        </div>
      </div >
      <div className={styles.note}>
      note 1: the answer has been round to the nearest 2 decimal
      </div>
    </div>
  );
}

export default Calculator;
