import React from 'react';
import { useSearchContext } from '../context/SearchContext';
import products from '../data/products.json';
import './SearchBlock.css'

function SearchBlock() {
  const { localSearchParams } = useSearchContext()
  const foundProducts  = products.filter(i => i.name.toLocaleLowerCase().search(localSearchParams.toLocaleLowerCase()) != -1)
  const searchOutput = () => {
    switch (localSearchParams.length) {
      case 0:
        return 
          <>
            <div>Sorry, nothing found for {localSearchParams}</div>
            <div>
                <button>SEE ALL Products</button>
            </div>
          </> 

      default:
        break;
    }
  }
  return (
    <>
    <div className='search-block'>
      <div>Products</div>
      <div>{localSearchParams}</div>
      <div>
        <button>view all </button>
      </div>
    </div> 


    </>
  );
}

export default SearchBlock;
