import React from 'react';
import { useLocation } from 'react-router-dom'
import './Banner.css'

function Banner() {
  const location = useLocation()
  const header = (location) => {
    switch (location.pathname.split('/')[3]) {
      case 'new-arrivals':
        return 'New Arrivals'
      case 'home-bath':
        return 'Home + Bath' 
      case 'kitchen-dinning':
        return 'Kitchen + Dinning' 
      case 'office-craft':
        return 'Office + Craft'
      case 'personal-goods':
        return 'Personal Goods'
      default:
        return 'Collections'
    }
  }
  const imgURL = `../img/cover/${location.pathname.split('/')[3]}.jpg`

  return (
    <div className='banner'>
      <div className='banner-location'>{header(location)}</div>
      <div>
        {imgURL ? <img src={require(`../img/cover/${location.pathname.split('/')[3]}.jpg`)} alt="" /> : ''}

      </div>

    </div>
  );
}

export default Banner;
