import React from 'react';
import { useCartContext } from '../context/CartContext';
import products from '../data/products.json';
import './Cart.css'


function Cart() {
  const { cartItems } = useCartContext()
  console.log(cartItems.map(i => i.id))

  return (
    <div className='cart'>
      <div>Continue Shopping</div>
      {products
      .filter(cur => cartItems.map(i => i.id).some(i => i === cur.id))
      .map(i => 
        <div className='cart-item-contaienr'>
          <img src={require('../img/products/'+i.imgUrl.split('/').slice(-2).join('/'))} alt="" />
          <div>{i.name}</div>
          <div>{i.price}</div>
          <div>
            <span>Item total:</span>
            <span>{i.price * cartItems.reduce((acc,cur) => (acc+cur.quantity),0) }</span>
          </div>
          <button>-</button>
          <div>{cartItems.reduce((acc,cur) => (acc+cur.quantity),0)}</div>
          <button>-</button>
        </div>
        )
      }
      <div className='cart-items'>items</div>
      <div className='subtotal'></div>
      <button>CHECKOUT</button>
    </div>
  );
}

export default Cart;
