const CurrenyFortmator = new Intl.NumberFormat(undefined, {
  currency: 'CNY',
  style: 'currency'
})

const formatCurrency = (number) => {
  const a = CurrenyFortmator.format(number).split('¥')
  return CurrenyFortmator.format(number)
}

export default formatCurrency
