import React from 'react';
import { useLocation } from 'react-router-dom'
import './Feature.css'

function Feature() {
  const location = useLocation()
  const header = (location) => {
    switch (location.pathname.split('/')[3]) {
      case 'new-arrivals':
        return 'New Arrivals'
        break;
      case 'home-bath':
        return 'Home + Bath'
        break;
      case 'kitchen-dinning':
        return 'Kitchen + Dinning'
        break;
      case 'office-craft':
        return 'Office + Craft'
        break;
      case 'personal-goods':
        return 'Personal Goods'
        break;
      default:
        break;
    }
  }

  return (
    <div className='feature'>
      <div className='location'>
        <span>Home &nbsp; / &nbsp; {header(location)} /</span>
        <span>Page</span>
      </div>
      <div className='sort'>
        <label for='sort_by'>Sort by:</label>
        <div className='sort-select'>
          <select name="feature" id='sort_by'>
            <option value="" onChange={(e) => {}}>Price: Low to High</option>
            <option value="">Price: High to Low</option>
          </select>
        </div>
      </div>
    </div>
  );
}

export default Feature;
