import React from 'react';
import { useCartContext } from '../context/CartContext';
import products from '../data/products.json';
import './Cart.css'


function Cart() {
  const { cartItems } = useCartContext()

  return (
    <div className='cart'>
      <div>Continue Shopping</div>
      {cartItems.map(i => 
        <div key={i.id}>
          <img src={require('../img/products/' + products.find(cur => cur.id === i.id).imgUrl.split('/').slice(-2).join('/'))} alt="" />
          <div>{products.find(cur => cur.id === i.id).name}</div>
          <div>{products.find(cur => cur.id === i.id).price}</div>
          <div>
            <span>Item total:</span>
            <span>{products.find(cur => cur.id === i.id).price * i.quantity}</span>
          </div>
          <button>-</button>
          <div>{i.quantity}</div>
          <button>+</button>
        </div>
        )}
      <div className='cart-items'>items</div>
      <div className='subtotal'></div>
      <button>CHECKOUT</button>
    </div>
  );
}

export default Cart;
