import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Cell } from 'recharts';
import { PieChart, Pie } from 'recharts';
import { useRecordsContext } from '../context/RecordsContext';
import last12Month from '../utilites/last12Month';
import generateRandomColorCode from '../utilites/ramdonColor';
import Nav from "./Nav"
import styles from './Present.module.css'

function Present() {
  const { records, options } = useRecordsContext()
  const [toggle, setToggle] = useState(true)
  const [pieData, setPieDate] = useState([])
  const [monthOn, setMonthOn] = useState("Jun")
  const today = new Date().toDateString()
  const data1 = [{ name: 'Page A', uv: 400, pv: 2400, amt: 2400 }, { name: 'Page B', uv: 800, pv: 2600, amt: 2200 }, { name: 'Page B', uv: 560, pv: 1200, amt: 2200 }];
  const data01 = [
    {
      "name": "水電",
      "value": 400
    },
    {
      "name": "餐飲",
      "value": 280
    },
    {
      "name": "日用",
      "value": 300
    },
    {
      "name": "交通",
      "value": 200
    }
  ];

  const createPieData = (year, month) => {
    const data = []
    for (let i = 0; i < options.length; i++) {
      data.push({
        "name": options[i],
        "value": records
          .filter(i => (i.date.split(" ")[3] === year && i.date.split(" ")[1] === month))
          .reduce((acc, cur) => {
            if (cur.category === options[i]) {
              return acc + cur.amount * 1
            } else {
              return acc + 0
            }
          }, 0),
        "color": generateRandomColorCode()
      })
    }
    return data.sort((a, b) => (b.value - a.value)).filter(i => i.value != 0)
  }

  const switchMonth = (date) => {
    document.getElementById(monthOn).style.fontWeight = "400"
    document.getElementById(date.slice(-3)).style.fontWeight = "600"
    setMonthOn(date.slice(-3))
    console.log(date.slice(-4), date.slice(4, 7))
    // setPieDate(createPieData(date.slice(-4), date.slice(4, 7)))
  }

  useEffect(() => {
    setPieDate(createPieData(today.slice(-4), today.slice(4, 7)))
  }, [records])

  // console.log(pieData)
  const color = ["#7ebed5"]

  return (
    <>
      <Nav></Nav>
      <div className={styles.container}>
        <div className={styles.swithChart}>
          <button onClick={() => setToggle(true)}>月支出分佈</button>
          <button onClick={() => setToggle(false)}>支出趨勢圖</button>
        </div>
        {toggle === true ?
          <div className={styles.chart}>
            <div className={styles.monthButtonArea}>
              {last12Month().map((cur, index) => {
                if (index === 0) {
                  return <button
                    id={cur.slice(-3)}
                    className={styles.monthButton}
                    key={index}
                    onClick={() => switchMonth(cur)}
                    style={{ fontWeight: "600" }}>
                    {cur}
                  </button>
                } else {
                  return <button
                    id={cur.slice(-3)}
                    className={styles.monthButton}
                    key={index}
                    onClick={() => switchMonth(cur)}>
                    {cur}
                  </button>
                }
              }
              )}
            </div>
            {pieData.length === 0 ?
              <div className={styles.noData}>
                <span>No data to display</span>
                <br />
                <Link to='/bookkeep/record'>去添加一筆！</Link>
                </div> :
              <>
                <PieChart width={300} height={300}>
                  <Pie
                    data={pieData}
                    dataKey="value"
                    ameKey="name"
                    cx="50%"
                    cy="50%"
                    outerRadius={140} >
                    {pieData.map((cur, index) => (
                      <Cell
                        key={`cell-${index}`}
                        fill={cur.color} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
                <div className={styles.optionsArea}>
                  {pieData.map((cur, index) =>
                  (<div className={styles.optionWrap} key={index}>
                    <div className={styles.block} style={{ backgroundColor: cur.color }}></div>
                    <span>{cur.name} {Math.round((cur.value / pieData.reduce((acc, cur) => (acc + cur.value), 0)) * 10000) / 100}%</span>
                  </div>)
                  )}
                </div>
              </>
            }
          </div> :
          <div className={styles.chart}>
            <div>支出趨勢</div>
            <LineChart width={400} height={400} data={data1}>
              <Line type="monotone" dataKey="pv" stroke="#8884d8" />
              <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
            </LineChart>
          </div>
        }
      </div>
    </>
  );
}

export default Present;
