import React, { useEffect } from 'react';
import { useLocation, useSearchParams } from 'react-router-dom';
import Navbar from './Navbar';
import Menu from './Menu';
import SearchBlock from './SearchBlock';
import SearchMark from './SearchMark';
import SearchBanner from './SearchBanner';
import Products from './Products';
import Reserve from './Reserve';
import Cart from './Cart';
import products from '../data/products.json';
import { useSearchContext } from '../context/SearchContext';

function Search() {
  const [searchParams, setSearchParams] = useSearchParams()
  const { localSearchParams } = useSearchContext()
  useEffect(() => {
      setSearchParams({q: localSearchParams})  
  },[localSearchParams])

  const foundProducts = products.filter(i => i.name.toLocaleLowerCase().indexOf(searchParams.get('q').toLocaleLowerCase()) !== -1)

  return (
    <>
      <Menu></Menu>
      <SearchBlock></SearchBlock>
      <div className='page-container'>
        <div className='margin-lr'>
          <Navbar></Navbar>
          <div className='page-inner'>
            <SearchBanner></SearchBanner>
            <div>
            {finalProducts
              .map(i => 
              <div className='product-container' key={i.id}>
                <div className='product-img-container'>
                  <img src={require('../img/products/'+i.imgUrl.split('/').slice(-2).join('/'))} alt="" />
                  <button className='add-to-cart' onClick={() => increaseCartItem(i.id)}>+ Add to Cart</button>
                </div>
                <div>{i.name}</div>
                <div>{formatCurrency(i.price)}</div>
              </div> )
              }
            </div>
            <Reserve></Reserve>
          </div>
        </div>
        <div className='mark'></div>
        <SearchMark></SearchMark>
      </div>
      <Cart></Cart>
    </>
  );
}

export default Search;
