import React, { useState } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Cell } from 'recharts';
import { PieChart, Pie } from 'recharts';
import { useRecordsContext } from '../context/RecordsContext';
import Nav from "./Nav"
import styles from './Present.module.css'

function Present() {
  const { records, options } = useRecordsContext()
  console.log(records )
  const data = []
  const data1 = [{ name: 'Page A', uv: 400, pv: 2400, amt: 2400 }, { name: 'Page B', uv: 800, pv: 2600, amt: 2200 }, { name: 'Page B', uv: 560, pv: 1200, amt: 2200 }];
  const data01 = [
    {
      "name": "水電",
      "value": 400
    },
    {
      "name": "餐飲",
      "value": 280
    },
    {
      "name": "日用",
      "value": 300
    },
    {
      "name": "交通",
      "value": 200
    }
  ];

  const pieData = (year, month) => {
    const data = []
    for (let i = 0; i < options.length; i++) {
      data.push({
        "name": options[i],
        "value": records.map()
      })
    }

    return data
  }


  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];
  const [toggle, setToggle] = useState(true)


  return (
    <>
      <Nav></Nav>
      <div className={styles.container}>
        {toggle === true ?
          <div className={styles.chart}>
            <PieChart width={730} height={250}>
              <Pie
                data={data01}
                dataKey="value"
                ameKey="name"
                cx="20%"
                cy="40%"
                outerRadius={80}
              >
                {data01.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
            <div></div>
          </div> :
          <div className={styles.chart}>
            <div>支出趨勢</div>
            <LineChart width={400} height={400} data={data1}>
              <Line type="monotone" dataKey="pv" stroke="#8884d8" />
              <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
            </LineChart>
          </div>
        }
      </div>
    </>
  );
}

export default Present;
