import React from 'react';
import Menu from './Menu';
import Navbar from './Navbar'
import Cart from './Cart';
import { useEffect } from 'react';
import { closeMenu } from '../utilities/closeMenu';
import './Ecommerce.css'
import { Link } from 'react-router-dom';

function Ecommerce() {
  useEffect(() => closeMenu())
  useEffect(() => {
    const navigation = document.querySelectorAll('.navigation a')
    for (let i = 0 ; i<navigation.length ; i++) {
      navigation[i].style.color = 'rgb(220, 220, 220)'
    }
    const search = document.querySelector('.navigation svg')
    search.style.fill = 'rgb(220, 220, 220)'
    const pageContainer = document.querySelector('.page-container')
    // window.scroll((e) => {
    //   e.preventDefault()
    // })
    HTMLElement.prototype.stopScroll = function(){
      this.scroll({top:this.scrollTop+1});
  }
    pageContainer.stopScroll();
  })

  return (
    <>
      <Menu></Menu>
      <div className='page-container'>
        <div className='home-cover'>
          <img src={require("../img/cover/Home_Page.jpg")} alt="home-page-cover" />
        </div>
        <div className='margin-lr'>
          <Navbar></Navbar>
          <div className='start-button'>
            <Link to='/ecommerce/collections'></Link>
          </div>
          <div className='outlet'></div>
        </div>
      </div>
      <Cart></Cart>
    </>
  );
}

export default Ecommerce
