import { createContext, useState, useContext } from "react";

const CartContext = createContext({})
export const useCartContext = () => {
  return useContext(CartContext)
} 


export const CartContextProvider = ( {children} ) => {
  const [cartItems, setCartItems] = useState([])
  const getCartItem = () => {
    return cartItems
  }
  const increaseCartItem = (id) => {
    if (cartItems.find(i => i.id === id) == null) {
      setCartItems([...cartItems, {id, quantity: 1}])
    } else {
      setCartItems.map(cur => {
        if (cur.id === id) {
          return {...cur, quantity: cur.quantity + 1}
        } else {
          return cur
        }
      })
    }
  }
  const decreaseCartItem = (id) => {
    if (cartItems.find(i => i.id === id).quantity === 1) {
      setCartItems(cartItems.filter(i => i.id!==id))
    } else {
      setCartItems.map(cur => {
        if (cur.id === id) {
          return {...cur, quantity: cur.quantity - 1}
        } else {
          return cur
        }
      })
    }
  }
  const removeCartItem = (id) => {
    setCartItems(cartItems.filter(i => i.id!==id))
  }

  return  <CartContext.Provider value={{getCartItem, increaseCartItem, decreaseCartItem, removeCartItem}}>
            {children} 
          </CartContext.Provider>
}
