import React from 'react';
import { useSearchContext } from '../context/SearchContext';
import { Link, useNavigate, useSearchParams } from 'react-router-dom';
import products from '../data/products.json';
import './SearchBlock.css'

function SearchBlock() {
  const { localSearchParams } = useSearchContext()
  const foundProducts = products.filter(i => i.name.toLocaleLowerCase().indexOf(localSearchParams.toLocaleLowerCase()) !== -1)
  const [searchParams, setSearchParams] = useSearchParams()
  const handleClcik = () => {
    setSearchParams({q: localSearchParams})
    console.log(123)
  }
  const searchOutput = () => {
    switch (foundProducts.length) {
      // 找不到
      case 0:
        return  <>
                  <div>Sorry, nothing found for {localSearchParams}.</div>
                  <div className='search-link'>
                  <Link to='/ecommerce/search'>SEE ALL PRODUCTS</Link>
                  </div>
                </>
      // 1-3個物件
      case 1:
      case 2:
      case 3:
        return  <>
                  <div>Products</div>
                  <div className='search-items'>
                  {foundProducts.map(i => 
                    <div key={i.id}>
                      <img src={require('../img/products/' + i.imgUrl.split('/').slice(-2).join('/'))} alt="" />
                      <div>{i.name}</div>
                    </div>
                    )}
                  </div>  
                </>
      // 未填寫留空
      case products.length:
        return <></>       
      // 找到大於3
      default:
        return  <>
                  <div>Products</div>
                  <div className='search-items'>
                  {foundProducts.slice(0,3).map(i => 
                    <div key={i.id} >  
                      <img src={require('../img/products/' + i.imgUrl.split('/').slice(-2).join('/'))} alt="" />
                      <div>{i.name}</div>
                    </div>
                    )}
                  </div>
                  <div className='search-link'>
                    {/* <button onClick={()=> navigate('/ecommerce/search')}>SEE All FOUND PRODUCTS</button> */}
                  <Link to='/ecommerce/search' onClick={() => handleClcik()}>SEE All FOUND PRODUCTS</Link>
                  </div>
                </>
    }
  }
  return (
    <div className='search-block'>
      {searchOutput()}
    </div>

  );
}

export default SearchBlock;
