import React, { Component } from 'react';



class Test extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isHot: true,
      wind: "breeze"
    }
    // this.setWeather = this.setWeather.bind(this)
  }
  setWeather() {
    this.setState({isHot: !this.state.isHot})
  }
  render() {

    return (
      <>
        <span onClick={()=> {
          console.log(this);
          // console.log(this.state.isHot);
          }}>今天天氣很{this.state.isHot? "炎熱": "涼爽"}, {this.state.wind}</span>

      </>
    );
  }
}

export default Test;
