import React from 'react';
import Header from './Header';
import Nav from './Nav';
import Footer from './Footer';
import styles from './Discography.module.css'
import data from '../data/discography.json'
import { useSearchParams, Link } from 'react-router-dom';

function Discography() {
  const [searchParams, setSearchParams] = useSearchParams({pageIndex: 1})
  return (
    <>
      <Header type='black'></Header>
      <Nav></Nav>
      <div className={styles.dicsography}>
        <div className={styles.dgpTitle}>DISCOGRAPHY</div>
        <div className={styles.albumList}>
          {data.reverse().map(i => 
            <div className={styles.album}>
              <img src={require("../img/"+i.imgUrl.split('/').slice(-2).join('/'))} alt={i.title} />
              <span>{i.title}</span>
              <span>{i.release_date}</span>
            </div>
          )}
        </div>
        <div className={styles.pageControl}>
          {/* <button onClick={() => setSearchParams(c => {
            if (c.get('pageIndex')>1){
              return {pageIndex: c.get('pageIndex')-1}
          } else {
            return {pageIndex: 1}
          }
          })}>Previous</button> */}
          {searchParams.get('pageIndex')*1 === 1 ?
          <button>Previous</button> :
          <Link to={`/twice/discography?pageIndex=${searchParams.get('pageIndex')*1-1}`}>
            <button>Previous</button>
          </Link>
          }
          <div>
            {searchParams.get('pageIndex')}/4
          </div>
          <Link to={'twice/discography/'}></Link>
          {/* <button onClick={() => setSearchParams(c => {
            if (c.get('pageIndex')<4){
              return {pageIndex: c.get('pageIndex')*1+1}
            } else {
              return {pageIndex: 4}
            }  
          })}>Next</button> */}
          {searchParams.get('pageIndex')*1 === Math.ceil(data.length/6) ?
          <button>Next</button> :
          <Link to={`/twice/discography?pageIndex=${searchParams.get('pageIndex')*1+1}`}>
            <button>Next</button>
          </Link>
          }
        </div>
      </div>
      <Footer type='black'></Footer>
    </>
  );
}

export default Discography;
