import React, { useState } from 'react';
import Nav from "./Nav"
import Calendar from 'react-calendar';
import { useRecordsContext } from '../context/RecordsContext';
import { ramdonId } from '../utilites/ramdonId';
import './Calendar.css'
import styles from "./Record.module.css"


function Record() {
  const [date, setDate] = useState(new Date());
  const [category, setCategory] = useState("餐飲")
  const [amount, setAmount] = useState("")
  const [memo, setMemo] = useState("")
  const [editCategory, setEditCategory] = useState("")
  const [editAmount, setEditAmount] = useState("")
  const [editMemo, setEditMemo] = useState("")
  const [editingRecordId, setEditingRecordId] = useState('')
  const { records, setRecords, options, setOptions } = useRecordsContext()


  const handleSubmit = (e) => {
    e.preventDefault()
    setRecords(records => {
      let id = ramdonId(6)
      while (records.find(record => record.id === id)) {
        let id = ramdonId(6)
      }
      return [...records, { id: id, date: date.toDateString(), category: category, memo: memo, amount: amount }]
    }
    )

    setCategory(e.target[0].options[e.target[0].selectedIndex].value)
    setAmount("")
    setMemo("")
  }
  const handleEditSubmit = (e) => {
    e.preventDefault();
    setRecords(records => [...records.filter(cur =>  cur.id != record.id)])
    setEditCategory("")
    setEditAmount("")
    setEditMemo("")
  }

  const toggleEdit = (id, category) => {
    if (id === "") {
      setEditingRecordId("")
      setEditCategory("")
      setEditAmount("")
      setEditMemo("")
    } else {
      setEditingRecordId(id)
      setEditCategory(category)
      setEditAmount("")
      setEditMemo("")
    }
  }

  return (
    <>
      <Nav></Nav>
      <br />
      <div className={""}>
        <Calendar onChange={setDate} value={date} />
      </div>
      <div>{date.toDateString()}</div>
      <div>
        {records.filter(i => i.date === date.toDateString()).map(record =>
          <div key={record.id}>
            {editingRecordId === record.id ?
              <>
                <form onSubmit={() => handleEditSubmit()}>
                  <label htmlFor="category"></label>
                  <select id="category" defaultValue={record.category} onChange={(e) => {
                    setEditCategory(e.target.options[e.target.selectedIndex].value);
                  }}>
                    {options.map((option, index) => <option key={index} value={option}>{option}</option>)}
                  </select>
                  <label htmlFor="memo"></label>
                  <input
                    id="memo"
                    type="text"
                    onChange={(e) => setEditMemo(e.target.value)}
                    value={editMemo}
                    placeholder={record.memo} />
                  <label htmlFor="amount"></label>
                  <input
                    id="amount"
                    type="number"
                    onChange={(e) => setEditAmount(e.target.value)}
                    value={editAmount}
                    required
                    placeholder={record.amount} />
                </form>
                <button type='submit'>save</button>
                <button onClick={() => setRecords(records => records.filter(cur => cur.id != record.id))}>delete</button>
                <button onClick={() => toggleEdit("")}>cancel</button>
              </> :
              <span>
                <span>{record.id}</span>{"-"}
                <span>{record.category}</span>{"-"}
                <span>{record.memo}</span>{"-"}
                <span>{record.amount}</span>
                <button onClick={() => {toggleEdit(record.id, record.category)}}>edit</button>
              </span>
            }
          </div>
        )}
      </div>
      <div>{editCategory}{editMemo}{editAmount}</div>
      <div>
        <form onSubmit={handleSubmit}>
          <label htmlFor="category"></label>
          <select name="" id="category" onChange={(e) => {
            setCategory(e.target.options[e.target.selectedIndex].value);
          }}>
            {options.map((option, index) =>
              <option key={index} value={option}>{option}</option>
            )}
          </select>
          <label htmlFor="memo"></label>
          <input
            id="memo"
            type="text"
            onChange={(e) => setMemo(e.target.value)}
            value={memo}
            placeholder='備註' />
          <label htmlFor="amount"></label>
          <input
            id="amount"
            type="number"
            onChange={(e) => setAmount(e.target.value)}
            value={amount}
            required
            placeholder='請輸入金額' />
          <button type='submit'>add</button>
        </form>
      </div>
    </>
  );
}

export default Record
